    private void nsfwCheckActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nsfwCheckActionPerformed
        if (this.nsfwCheck.isSelected()) {
          this.nsfwPercentageBottomLabel.setEnabled(true);
          this.nsfwPercentageField.setEnabled(true);
          this.nsfwPercentageLabel.setEnabled(true);
        } else {
          this.nsfwPercentageBottomLabel.setEnabled(false);
          this.nsfwPercentageField.setEnabled(false);
          this.nsfwPercentageLabel.setEnabled(false);
        } 
    }//GEN-LAST:event_nsfwCheckActionPerformed

    private void explorerButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_explorerButtonActionPerformed

          JFileChooser fileChooser = new JFileChooser();
          fileChooser.setFileSelectionMode(2);
          int result = fileChooser.showOpenDialog(null);
          if (result == 0) {
            File selectedFile = fileChooser.getSelectedFile();
            System.out.println("Hai selezionato: " + selectedFile.getAbsolutePath());
            this.path = selectedFile.getAbsolutePath();
            this.pathLabel.setText(this.path);
          } else if (result == 1) {
            System.out.println("Nessun file selezionato");
          } 
    }//GEN-LAST:event_explorerButtonActionPerformed

    private void jpgSliderStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_jpgSliderStateChanged
        this.jLabel7.setText(String.valueOf(this.jpgSlider.getValue()));
    }//GEN-LAST:event_jpgSliderStateChanged

    private void floodButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_floodButtonActionPerformed
        try {
            progressBar.setValue(0);
            AnimeFlooding.subDirNum = AnimeFlooding.contaSottocartelle(this.path) + 1;
            AnimeFlooding.avanzamento = 100 / this.jpgSlider.getValue() * AnimeFlooding.subDirNum;
            System.out.println(AnimeFlooding.subDirNum);
            System.out.println("n: " + this.jpgSlider.getValue());
            try {
                AnimeFlooding.scorrereSottocartelle(this.path, this.jpgSlider.getValue(), this.nsfwCheck.isSelected(), Integer.parseInt(this.nsfwPercentageField.getText()));
            } catch (Exception ex) {
                java.util.logging.Logger.getLogger(Gui.class.getName()).log(java.util.logging.Level.SEVERE, null, ex);
            }
            progressBar.setValue(100);
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }//GEN-LAST:event_floodButtonActionPerformed

